<template>
  <main class="news" v-html="news"></main>
</template>

<script>

  export default {
    data() {
	  return {
	    news: ''
	  }    
    },
    created() {
      this.fetch()
    },
    refresh() {
      this.fetch()
  	},
  	methods: {
  	  fetch() {
	  	this.$http({
	      url: '/news/index,page=1'
	    }, (err, result) => {
	      this.news = result
	    })
  	  }
  	}
  }

</script>